mapper.select:
/* CategoryMapper.getMaxSeq */
SELECT NVL(MAX((SEQ)), 0) + 1
FROM CATEGORIES
WHERE USER_ID = #{userId}

/* CategoryMapper.checkCtgNm */
SELECT COUNT(*)
FROM CATEGORIES
WHERE USER_ID = ${userId}
  AND CTG_NM = #{ctgNm}

mapper.insert:
/* CategoryMapper.addCategory */
INSERT INTO CATEGORIES(USER_ID, CTG_NM, SEQ)
VALUES (#{userId}, #{ctgNm}, #{seq})

mapper.update:
/* CategoryMapper.updateCategory */
UPDATE CATEGORIES
SET CTG_NM     = #{ctgNm},
    UPDATED_AT = SYSDATE
WHERE ID = #{id}

mapper.delete:
/* CategoryMapper.deleteCategoryById */
DELETE
FROM CATEGORIES
WHERE ID = #{id}

mapper.update:
/* CategoryMapper.updateCtgSeq */
UPDATE CATEGORIES
SET SEQ        = #{seq},
    UPDATED_AT = SYSDATE
WHERE ID = #{id}

/* CategoryMapper.updateOtherCtgsSeqByUser */

UPDATE CATEGORIES CTG
SET SEQ = (SELECT CASE
                      WHEN SEQ > ${oldSeq} AND SEQ <= ${seq}
                          THEN SEQ - ${seq}
                      WHEN SEQ < ${oldSeq} AND SEQ >= ${seq}
                          THEN SEQ + ${seq}
                      ELSE SEQ
                      END
           FROM CATEGORIES SUB
           WHERE CTG.ID = SUB.ID)
WHERE USER_ID = #{userId}
  AND ((SEQ < ${oldSeq} AND SEQ >= ${seq}) OR (SEQ > ${oldSeq} AND SEQ <= ${seq}))
  AND ID <> CTG.ID

mapper.select:
/* CategoryMapper.seqsMinusOneIfCtgDeleted */
UPDATE CATEGORIES
SET SEQ = SEQ - 1

WHERE USER_ID = #{userId}
  AND SEQ > #{seq}
